import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

export default class AlbumCard extends Component {
  render() {
    const { artistName, artworkUrl100, collectionId, collectionName } = this.props;
    return (
      <Link
        to={ `/album/${collectionId}` }
        data-testid={ `link-to-album-${collectionId}` }
        className='bg-zinc-800'
      >
        <div className='rounded-lg bg-black hover:opacity-90 m-4'>
          <img src={ artworkUrl100 } alt={ `Capa do album ${collectionName}` } className='w-full rounded-lg' />
          <h3>
            {collectionName}
            {' '}
          </h3>
          <p>{artistName}</p>
        </div>
      </Link>
    );
  }
}

AlbumCard.propTypes = {
  artistName: PropTypes.string,
  artworkUrl100: PropTypes.string,
  collectionId: PropTypes.number,
  collectionName: PropTypes.string,
}.isRequired;
