import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import MusicsCard from '../components/MusicsCard';
import getMusics from '../services/musicsAPI';
import Carregando from '../components/Carregando';

class Album extends Component {
  state = ({
    loading: true,
    musicasAlbum: [],
  });

  componentDidMount() {
    this.retornaMusicas();
  }

  retornaMusicas = async () => {
    const { match: { params: { id } } } = this.props;
    console.log(id);
    const musicas = await getMusics(id);
    console.log(musicas);
    this.setState({
      musicasAlbum: musicas,
      loading: false,
    });
  };

  render() {
    const { musicasAlbum, loading } = this.state;
    return (
      <div data-testid="page-album" className='flex bg-black text-white font-bold'>
        <aside className='p-4 font-semibold text-zinc-400 w-1/6 p-4 font-semibold min-h-screen text-zinc-400 bg-opacity-10 bg-white'>
          <nav className=' p-2 text-xs font-semibold '>
            <Header />

          </nav>


        </aside>

        {loading && <Carregando />}
        <section className='flex flex-col'>
          {musicasAlbum.map((musica, i) => {
            if (i === 0) {
              return (
                <div key={ musica.collectionId }>
                  <h2 data-testid="artist-name">{musica.artistName}</h2>
                  <img src={ musica.artworkUrl100 } alt="Capa do Álbum" />
                  <p data-testid="album-name">{musica.collectionName}</p>
                </div>
              );
            }
            return (
              <MusicsCard
                key={ musica.trackId }
                { ...musica }
              />
            );
          })}
        </section>
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }) }.isRequired;

export default Album;
