import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { createUser } from '../services/userAPI';
import Carregando from '../components/Carregando';

class Login extends Component {
  state = {
    disableButton: true,
    usuario: '',
    loading: false,
  };

  changeButton = ({ target }) => {
    const { name, value } = target;
    // const { usuario } = this.state;
    this.setState({
      [name]: value,
    }, () => {
      const { usuario } = this.state;
      const validationButton = usuario.length <= 2;
      this.setState({
        disableButton: validationButton,
      });
    });
    // console.log(usuario);
  };

  saveUser = async (event) => {
    event.preventDefault();
    const { usuario } = this.state;
    const { history } = this.props;

    this.setState({
      loading: true,
    });

    await createUser({ name: usuario });
    history.push('/search');
  };

  render() {
    const { disableButton, loading } = this.state;

    return (
      <div data-testid="page-login" className='flex items-center justify-center min-h-screen bg-gradient-to-b from-black/70  via-black/80 to-black'>

          <form className='flex items-center justify-center flex-col hadow-lg w-1/2 h-[500px] rounded-lg bg-gradient-to-b from-black  via-black/80 to-black/70'> 
            <svg viewBox="0 0 167.5 167.5" xmlns="http://www.w3.org/2000/svg" fill="#FFFFFF">
              <path d="M83.75,0C37.5,0,0,37.5,0,83.75s37.5,83.75,83.75,83.75,83.75-37.5,83.75-83.75S130,0,83.75,0Zm38.64,121.45c-1.39,2.32-4.35,3.08-6.67,1.69-18.28-10.89-41.23-13.36-68.37-7.91-2.64,.55-5.3-1.09-5.85-3.75s1.09-5.3,3.75-5.85c29.55-6.4,54.67-3.5,75.94,8.55,2.33,1.38,3.09,4.34,1.7,6.67Zm9.25-22.35c-1.73,2.84-5.42,3.74-8.26,2.01-20.9-12.74-52.7-16.45-77.4-9.75-3.13,.83-6.37-1.04-7.21-4.17-.83-3.13,1.04-6.37,4.17-7.21,28.32-7.52,63.25-3.31,87.07,11.11,2.84,1.73,3.74,5.42,2.01,8.26Zm.85-23.33C115.48,71.56,86.98,65.63,60.88,72.5c-3.6,.9-7.27-1.24-8.17-4.84-.9-3.6,1.24-7.27,4.84-8.17,29.85-7.45,61.87-.61,85.25,13.92,3.15,1.88,4.2,5.96,2.31,9.11-1.88,3.15-5.96,4.2-9.11,2.31Z"/>
            </svg>
            <h1 className="text-3xl font-bold text-white">
            Entrar no Spotify
    </h1>

            <input
              type="text"
              name="usuario"
              id=""
              // data-testid="login-name-input"
              onChange={ this.changeButton }
              className="bg-red-"
            />

            <button
              data-testid="login-submit-button"
              disabled={ disableButton }
              onClick={ this.saveUser }
              className="bg-green-500 rounded-full w-40 h-10 font-bold"
            >
              Entrar
            </button>
          </form>


      </div>

    );
  }
}

Login.propTypes = {
  history: PropTypes.func,
}.isRequired;

export default Login;
