import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { getUser } from '../services/userAPI';
import Carregando from './Carregando';
import { Home as HomeIcon, Library, Search } from 'lucide-react';

class Header extends Component {
  state = {
    usuario: '',
    loading: true,
  };

  async componentDidMount() {
    const retorno = await getUser();
    console.log(retorno);
    this.setState({
      usuario: retorno,
      loading: false,
    });
  }

  render() {
    const { usuario, loading } = this.state;
    return (
      <div className='' data-testid="header-component">
        {loading ? <Carregando /> : (
          <h1 data-testid="header-user-name">
            {usuario.name}
          </h1>
        )}

        <ul className='flex flex-col'>
          <li>
            <Link className='flex flex-row items-center text-xs font-semibold transition-transform duration-500 transform hover:scale-110' data-testid="link-to-profile" to="/profile">
              <HomeIcon /> Home
            </Link>
          </li>
          <li>
            <Link className='flex flex-row items-center text-xs font-semibold transition-transform duration-500 transform hover:scale-110' data-testid="link-to-search" to="/search">
              <Search /> Search
            </Link>
          </li>
          <li>
            <Link className='flex flex-row items-center text-xs font-semibold transition-transform duration-500 transform hover:scale-110' data-testid="link-to-favorites" to="/favorites">
              <Library /> Library
            </Link>
          </li>
        </ul>
      </div>
    );
  }
}

export default Header;
