import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { getUser } from '../services/userAPI';
import Carregando from './Carregando';

class Header extends Component {
  state = ({
    usuario: '',
    loading: true,
  });

  async componentDidMount() {
    const retorno = await getUser();
    console.log(retorno);
    this.setState({
      usuario: retorno,
      loading: false,
    });
  }

  render() {
    const { usuario, loading } = this.state;
    return (

      <div data-testid="header-component">
        {loading ? <Carregando /> : (
          <h1 data-testid="header-user-name">
            {' '}
            { usuario.name }
          </h1>)}

        <ul>
          <li><Link data-testid="link-to-search" to="/search">Pesquisa</Link></li>
          <li><Link data-testid="link-to-favorites" to="/favorites">Favoritos</Link></li>
          {/* <li><Link data-testid="link-to-profile" to="/profile">Perfil</Link></li> */}
        </ul>

      </div>
    );
  }
}

export default Header;
