import React, { Component } from 'react';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Carregando from './Carregando';
import Resultado from './Resultado';
import { Home, Home as HomeIcon, Library, Search } from 'lucide-react';

class SearchMusic extends Component {
  state = ({
    albumList: [],
    clicado: false,
    disableButton: true,
    inputBusca: '',
    termoBusca: '',
    loading: false,
  });

  handleChange = ({ target }) => {
    const { value } = target;
    const validate = value.length > 1;

    this.setState({
      disableButton: !validate,
      termoBusca: value,
      inputBusca: value,
      clicado: false,
    });
    // const { disableButton } = this.state;
    // // console.log(value, disableButton, validate);
  };

  buscar = async (e) => {
    this.setState({
      loading: true,
    });
    e.preventDefault();
    const { termoBusca } = this.state;
    const resultApi = await searchAlbumsAPI(termoBusca);
    this.setState({
      clicado: true,
      inputBusca: '',
      albumList: resultApi,
      loading: false,

    });
  };

  render() {
    const { disableButton, clicado,
      termoBusca, inputBusca, loading,
      albumList } = this.state;

    console.log(this.state);
    return (
      (loading ? <Carregando />
        : (
          <form>
            <input
              type="text"
              name="inputBusca"
              id="pesquisa"
              data-testid="search-artist-input"
              onChange={ this.handleChange }
              value={ inputBusca }
              placeholder="ðŸ”Ž O que vocÃª quer ouvir?"
              className='px-4 py-3 rounded-full mb-4 w-full'
            />
            <button
              type="button"
              data-testid="search-artist-button"
              disabled={ disableButton }
              onClick={ this.buscar }
            >
              Pesquisar
            </button>
            {!clicado
              ? <p />
              : (
                <Resultado
                  termoBusca={ termoBusca }
                  albumList={ albumList }
                />) }
          </form>
        )
      )
    );
  }
}

export default SearchMusic;
